$(document).ready(function () {
    var url = window.location.pathname;
    var url_parent = url.substr(1, 4);
    if (url != "") {
        $("a[href^='" + url + "']").addClass('active');
        $("a[href^='" + url_parent + "']").addClass('active').next().show();
    }
    $('#menu_social > li.sub > a').click(function() {
        $('#menu_social li ul').slideUp();
        if ($(this).next().is(":visible")){
            $(this).next().slideUp();
        } else {
            $(this).next().slideToggle();
        }
        $('#menu_social li a').removeClass('active');
        $(this).addClass('active');
        return false;
    });
   
    $('.sub ul li a').click(function(){
        $('.sub ul li a').removeClass('active');
        $(this).addClass('active');
    });
});